use strict;
use Test::More tests => 14;
use warnings;

# use a BEGIN block so we print our plan before MyModule is loaded
BEGIN {
    use_ok('CompBio');
    $| = 1;
} # BEGIN
my $DEBUG = 0;

ok(-d $GENOME_HOME,"check GENOME_HOME");
ok($DBSERVER,"DBSERVER defined?");
ok($DBHOST,"DBHOST defined?");

my @na_seqs;
while (<DATA>) {
    chomp;
    $_ = join("\t",split); # fix tab wreckage reading from data
    push(@na_seqs,$_) if $_;
} # init @seqs
ok(@na_seqs == 10,"seqs loaded from DATA"); # succesfully loaded test data

my $cbc = CompBio->new;
ok(ref($cbc) eq "CompBio" && $cbc->{'_created'},"new \$cbc object created");

my $type = $cbc->check_type(\@na_seqs);
ok($type eq "CDNA","check_type");
print "check_type returned $type.\n" if $DEBUG >= 1;
(my $junk,my $rawseq_w) = split(/\t/,$na_seqs[2]);
($junk,my $rawseq_c) = split(/\t/,$na_seqs[6]);

my @seqs;
foreach (@na_seqs) {
    my ($id,$naseq) = split;
    my $sref_aaseq = "";
    if ($id =~ /6$|7$|10$/) {
        $sref_aaseq = $cbc->dna_to_aa(\$naseq,(C => 1,SEQFIX => 1,DEBUG => $DEBUG));
    } # C strand seqs
    else { $sref_aaseq = $cbc->dna_to_aa(\$naseq,(SEQFIX => 1,DEBUG => $DEBUG)) }
    print "$id\t$naseq\n$id\t$$sref_aaseq\n" if $DEBUG > 1;
    next if $$sref_aaseq =~ /\./; # bad translation
    push(@seqs,(join("\t",($id,$$sref_aaseq))));
} # convert to aa
ok(@seqs == 10 && $cbc->check_type(\@seqs) eq "TBL","dna_to_aa");
print "$na_seqs[6]\n" if $DEBUG > 1;
print scalar(@seqs),"\n$seqs[6]\n" if $DEBUG;

$seqs[6] .= "\toptional data 1\ttest2!";

my $AR_faseqs = $cbc->tbl_to_fa(\@seqs);
ok($AR_faseqs && $cbc->check_type($AR_faseqs) eq "FA","tbl_to_fa");
print "$$AR_faseqs[6]\n" if $DEBUG;

my $AR_igseqs = $cbc->tbl_to_ig(\@seqs);
ok($AR_igseqs && $cbc->check_type($AR_igseqs) eq "IG","tbl_to_ig");
print "$$AR_igseqs[6]\n" if $DEBUG;

my $AR_tblseqs = $cbc->fa_to_tbl($AR_faseqs,(CLEAN => 1));
ok($AR_tblseqs && $cbc->check_type($AR_tblseqs) eq "TBL","fa_to_tbl");
print "$$AR_tblseqs[6]\n" if $DEBUG;

$AR_tblseqs = $cbc->ig_to_tbl($AR_igseqs,(CLEAN => 1));
ok($AR_tblseqs && $cbc->check_type($AR_tblseqs) eq "TBL","ig_to_tbl");
print "$$AR_tblseqs[6]\n" if $DEBUG;

my @sixframe = split(/\n/,$cbc->six_frame(\$rawseq_w,(ID => "b0003-cdna")));
ok(@sixframe && $cbc->check_type(\@sixframe) eq "TBL","six_frame");

@sixframe = split(/\n/,$cbc->six_frame(\$rawseq_c,(ID => "b0007-cdna")));
my @sfcheck = grep /b0007-cdna_1431_1/, @sixframe;
$sfcheck[0] =~ s/-cdna_1431_1//;
$$AR_tblseqs[6] =~ s/\toptional.+//;
ok(@sfcheck == 1 && $sfcheck[0] eq $$AR_tblseqs[6],"six_frame complement check");
print "$sfcheck[0]\n$$AR_tblseqs[6]\n" if $DEBUG;


